mapper.select:
SELECT PLAYER_NUMBER, PLAYER_NAME, PLAYER_BACKNUMBER, PLAYER_VIEW_CNT, ADMIN_NUMBER, ADMIN_ID, FAME_NAME, WITH_NAME, POSITION_NAME, PLAYER_FILE_UPLOAD_PATH, PLAYER_FILE_UUID, PLAYER_FILE_NAME FROM ( SELECT ROWNUM AS RNUM, PLAYER_NUMBER, PLAYER_NAME, PLAYER_BACKNUMBER, PLAYER_VIEW_CNT, ADMIN_NUMBER, ADMIN_ID, FAME_NAME, WITH_NAME, POSITION_NAME, PLAYER_FILE_UPLOAD_PATH, PLAYER_FILE_UUID, PLAYER_FILE_NAME FROM ( SELECT P.PLAYER_NUMBER, P.PLAYER_NAME, P.PLAYER_BACKNUMBER, P.PLAYER_VIEW_CNT, P.ADMIN_NUMBER, A.ADMIN_ID, F.FAME_NAME, W.WITH_NAME, POS.POSITION_NAME, J.PLAYER_FILE_UPLOAD_PATH, J.PLAYER_FILE_UUID, J.PLAYER_FILE_NAME FROM TBL_PLAYER P JOIN TBL_ADMIN A ON P.ADMIN_NUMBER = A.ADMIN_NUMBER LEFT JOIN TBL_FAME F ON P.FAME_NUMBER = F.FAME_NUMBER LEFT JOIN TBL_WITH W ON P.WITH_NUMBER = W.WITH_NUMBER LEFT JOIN TBL_POSITION POS ON P.POSITION_NUMBER = POS.POSITION_NUMBER LEFT JOIN ( SELECT PLAYER_FILE_NUMBER, PLAYER_FILE_UPLOAD_PATH, PLAYER_FILE_UUID, PLAYER_FILE_NAME, PLAYER_NUMBER FROM ( SELECT PLAYER_FILE_NUMBER, PLAYER_FILE_UPLOAD_PATH, PLAYER_FILE_UUID, PLAYER_FILE_NAME, PLAYER_NUMBER, RANK() OVER (PARTITION BY PLAYER_NUMBER ORDER BY PLAYER_FILE_NUMBER) RK FROM TBL_PLAYER_FILE PF ) WHERE RK = 1 ) J ON P.PLAYER_NUMBER = J.PLAYER_NUMBER WHERE P.WITH_NUMBER = 1 ORDER BY P.PLAYER_NUMBER DESC ) S1
WHERE ROWNUM <= #{page} * #{amount}
) S2 WHERE RNUM > (#{page} - 1) * #{amount}
